static bool EnMode;                    /* Enable/Disable SCI in speed mode */
static word SerFlag;                   /* Flags for serial communication */
                                       /* Bits: 0 - OverRun error */
                                       /*       1 - Framing error */
                                       /*       2 - Parity error */
                                       /*       3 - Char in RX buffer */
                                       /*       4 - Full TX buffer */
                                       /*       5 - Running int from TX */
                                       /*       6 - Full RX buffer */
                                       /*       7 - Noise error */
                                       /*       8 - Idle character  */
                                       /*       9 - Break detected  */
                                       /*      10 - Unused */
                                       /*      11 - Unused */
static word ErrFlag;                   /* Error flags mirror of SerFlag */
static word Debug_UART1_InpLen;        /* Length of input buffer's content */
static word InpIndexR;                 /* Index for reading from input buffer */
static word InpIndexW;                 /* Index for writing to input buffer */
static Debug_UART1_TComData InpBuffer[Debug_UART1_INP_BUF_SIZE]; /* Input buffer for SCI communication */
static Debug_UART1_TComData BufferRead; /* Input char for SCI communication */
static word Debug_UART1_OutLen;        /* Length of output bufer's content */
static word OutIndexR;                 /* Index for reading from output buffer */
static word OutIndexW;                 /* Index for writing to output buffer */
static Debug_UART1_TComData OutBuffer[Debug_UART1_OUT_BUF_SIZE]; /* Output buffer for SCI communication */

